mapper.insert:
INSERT ALL

	INTO TRIP(CONTENT_ID
			 ,CONTENT_TYPE_ID
			 ,TITLE
			 ,ADDR1
			 ,ADDR2
			 ,ZIP_CODE
			 ,AREA_CODE
			 ,FIRST_IMAGE2)
	VALUES(#{trip.contentId}
		  ,#{trip.contentTypeId}
		  ,#{trip.title}
		  ,#{trip.addr1}
		  ,#{trip.addr2}
		  ,#{trip.zipCode}
		  ,#{trip.areaCode}
		  ,#{trip.firstImage2})

SELECT * FROM DUAL
